import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Typography, Grid, Card, CardContent, Button, CircularProgress } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

// Criando o tema escuro
const theme = createTheme({
  palette: {
    mode: 'dark', // Modo escuro
    primary: {
      main: '#90caf9', // Cor principal
    },
    background: {
      default: '#121212', // Cor de fundo (página)
      paper: '#1c1c1c', // Fundo dos componentes (cards, etc.)
    },
    text: {
      primary: '#ffffff', // Cor do texto (principal)
    },
  },
});

// Componente principal
function App() {
  const [status, setStatus] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/status')
      .then((response) => {
        setStatus(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError('Erro ao carregar os dados');
        setLoading(false);
      });
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <Container>
        <Typography variant="h3" gutterBottom align="center" color="textPrimary">
          Monitoramento de Recarga de Veículos Elétricos
        </Typography>

        {loading ? (
          <CircularProgress color="primary" />
        ) : error ? (
          <Typography color="error" align="center">
            {error}
          </Typography>
        ) : (
          <Grid container spacing={3} justifyContent="center">
            {status.map((veiculo, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Card variant="outlined">
                  <CardContent>
                    <Typography variant="h5" component="div">
                      {veiculo.nome}
                    </Typography>
                    <Typography color="textSecondary">
                      Status: {veiculo.status}
                    </Typography>
                    <Typography color="textSecondary">
                      Fonte de Energia: {veiculo.fonteEnergia}
                    </Typography>
                    <Typography color="textSecondary">
                      Hora de Início: {veiculo.horaInicio}
                    </Typography>
                    <Button variant="contained" color="primary" fullWidth>
                      Detalhes
                    </Button>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Container>
    </ThemeProvider>
  );
}

export default App;
