import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Tickets = () => {
  const [tickets, setTickets] = useState([]);
  const [statusOptions, setStatusOptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [statusId, setStatusId] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      navigate('/login');
    } else {
      // Carregar os tickets do usuário
      axios
        .get('http://localhost:5000/api/tickets', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setTickets(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Erro ao carregar os tickets:', error); // Adicionado log de erro
          setError('Erro ao carregar os tickets');
          setLoading(false);
        });
  
      // Carregar as opções de status de recarga
      axios
        .get('http://localhost:5000/api/status')
        .then((response) => {
          setStatusOptions(response.data);
        })
        .catch((error) => {
          console.error('Erro ao carregar os status de recarga:', error); // Adicionado log de erro
          setError('Erro ao carregar os status de recarga');
        });
    }
  }, [navigate]);  

  const handleCreateTicket = () => {
    const token = localStorage.getItem('authToken');
  
    if (!statusId) {
      setError('Selecione um status para o ticket');
      return;
    }
  
    // Enviar requisição para criar um novo ticket
    axios
      .post(
        'http://localhost:5000/api/tickets',
        { statusId },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      )
      .then((response) => {
        setTickets([...tickets, response.data]); // Adiciona o novo ticket à lista
        setStatusId(''); // Limpa o campo de status
      })
      .catch((error) => {
        console.error('Erro ao criar o ticket:', error); // Log do erro
        setError('Erro ao criar o ticket');
      });
  };  

  if (loading) return <div>Carregando...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Tickets</h1>
      <div>
        <h3>Criar Ticket</h3>
        <select
          value={statusId}
          onChange={(e) => setStatusId(e.target.value)}
        >
          <option value="">Selecione o Status</option>
          {statusOptions.map((status) => (
            <option key={status.id} value={status.id}>
              {status.nome}
            </option>
          ))}
        </select>
        <button onClick={handleCreateTicket}>Criar Ticket</button>
      </div>
      <ul>
        {tickets.map((ticket) => (
          <li key={ticket.id}>
            Ticket ID: {ticket.id} - Status ID: {ticket.status_id}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tickets;
