import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Tickets = () => {
  const [tickets, setTickets] = useState([]);
  const [statusOptions, setStatusOptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [nome, setNome] = useState('');
  const [status, setStatus] = useState('');
  const [fonteEnergia, setFonteEnergia] = useState('');
  const [horaInicio, setHoraInicio] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      navigate('/login'); // Redireciona para o login se não houver token
    } else {
      // Carregar os tickets do usuário
      axios
        .get('http://localhost:5000/api/tickets/user', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setTickets(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Erro ao carregar os tickets do usuário:', error);
          setError('Erro ao carregar os tickets');
          setLoading(false);
        });

      // Carregar as opções de status de recarga
      axios
        .get('http://localhost:5000/api/status')
        .then((response) => {
          setStatusOptions(response.data);
        })
        .catch((error) => {
          console.error('Erro ao carregar os status de recarga:', error);
          setError('Erro ao carregar os status de recarga');
        });
    }
  }, [navigate]);

  const handleCreateTicket = () => {
    const token = localStorage.getItem('authToken');

    if (!nome || !status || !fonteEnergia || !horaInicio) {
      setError('Todos os campos são obrigatórios');
      return;
    }

    // Enviar requisição para criar um novo ticket
    axios
      .post(
        'http://localhost:5000/api/tickets',
        { nome, status, fonteEnergia, horaInicio },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      )
      .then((response) => {
        setTickets([...tickets, response.data]); // Adiciona o novo ticket à lista
        setNome(''); // Limpa os campos
        setStatus('');
        setFonteEnergia('');
        setHoraInicio('');
      })
      .catch((error) => {
        console.error('Erro ao criar o ticket:', error);
        setError('Erro ao criar o ticket');
      });
  };

  if (loading) return <div>Carregando...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Tickets</h1>

      {/* Formulário de Criação de Ticket */}
      <div>
        <h3>Criar Ticket</h3>
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
        />
        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        >
          <option value="">Selecione o Status</option>
          {statusOptions.map((status) => (
            <option key={status.id} value={status.id}>
              {status.nome}
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Fonte de Energia"
          value={fonteEnergia}
          onChange={(e) => setFonteEnergia(e.target.value)}
        />
        <input
          type="text"
          placeholder="Hora de Início"
          value={horaInicio}
          onChange={(e) => setHoraInicio(e.target.value)}
        />
        <button onClick={handleCreateTicket}>Criar Ticket</button>
      </div>

      {/* Lista de Tickets do Usuário */}
      <h3>Meus Tickets</h3>
      <ul>
        {tickets.map((ticket) => (
          <li key={ticket.id}>
            Ticket ID: {ticket.id} - Nome: {ticket.nome} - Status: {ticket.status} - Fonte de Energia: {ticket.fonteEnergia} - Hora de Início: {ticket.horaInicio}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tickets;
