import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Tickets = () => {
  const [statusList, setStatusList] = useState([]); // Lista de status
  const [tickets, setTickets] = useState([]); // Lista de tickets
  const [selectedUser, setSelectedUser] = useState(''); // Usuário selecionado
  const [filteredTickets, setFilteredTickets] = useState([]); // Tickets filtrados
  const user = JSON.parse(localStorage.getItem('user')); // Dados do usuário
  const navigate = useNavigate(); // Navegação entre páginas

  useEffect(() => {
    if (!user) {
      navigate('/login'); // Redireciona para o login caso não haja usuário logado
    }

    // Carregar lista de status
    axios.get('http://localhost:5000/api/status')
      .then(response => {
        setStatusList(response.data);
      });

    // Carregar tickets de recarga
    axios.get('http://localhost:5000/api/recarga-tickets')
      .then(response => {
        setTickets(response.data);
        setFilteredTickets(response.data);  // Exibe todos os tickets inicialmente
      });
  }, [navigate, user]);

  // Função para criar um novo ticket de recarga
  const handleCreateTicket = (statusId) => {
    axios.post('http://localhost:5000/api/recarga-tickets', {
      userId: user.id,
      statusId
    }).then(response => {
      setTickets([...tickets, response.data]);
      setFilteredTickets([...tickets, response.data]);
    });
  };

  // Função para filtrar tickets por usuário
  const handleFilterTickets = (userId) => {
    const filtered = tickets.filter(ticket => ticket.user_id === userId);
    setFilteredTickets(filtered);
  };

  return (
    <div>
      <h2>Tickets de Recarga</h2>
      
      {/* Filtro por usuário */}
      <div>
        <label>Filtrar por usuário</label>
        <select onChange={(e) => handleFilterTickets(e.target.value)} value={selectedUser}>
          <option value="">Todos</option>
          {/* Aqui você pode adicionar os usuários carregados da API */}
          {/* Exemplo de como os usuários podem ser filtrados */}
          {/* userList.map(user => (
            <option key={user.id} value={user.id}>{user.nome}</option>
          )) */}
        </select>
      </div>

      {/* Criar um novo ticket de recarga */}
      <div>
        <h3>Criar Ticket de Recarga</h3>
        <label>Fonte de Energia</label>
        <select>
          {statusList.map(statusItem => (
            <option key={statusItem.id} value={statusItem.id}>{statusItem.nome}</option>
          ))}
        </select>
        {/* Corrigido para garantir que o `statusItem.id` seja passado corretamente */}
        <button onClick={() => handleCreateTicket(statusList[0]?.id)}>Criar Ticket</button>
      </div>

      {/* Exibir tickets filtrados */}
      <div>
        <h3>Tickets Existentes</h3>
        {filteredTickets.map(ticket => (
          <div key={ticket.id}>
            <p>Ticket ID: {ticket.id}, Status ID: {ticket.status_id}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tickets;
